import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-submission-form',
  templateUrl: './submission-form.component.html',
  styleUrls: ['./submission-form.component.css']
})
export class SubmissionFormComponent {
  fullName: string = '';
  email: string = '';
  comments: string = '';
  gitLink: string = '';
  videoLink: string = '';

  constructor(private http: HttpClient) { }

  onSubmit() {
    const formData = {
      fullName: this.fullName,
      email: this.email,
      comments: this.comments,
      gitLink: this.gitLink,
      videoLink: this.videoLink
    };
    console.log(formData);
    this.http.post<any>('http://localhost:9000/student/submit', formData).subscribe(
      (response) => {
        console.log('Assignment submitted successfull...', response.message);
        alert('Assignment submitted successfull')
        window.location.reload();
      },
      (error) => {
        console.error('Error submitting attendance..', error);
      }
    );
  }
}
<div class="container">
    <form (ngSubmit)="onSubmit()">
        <div class="form-floating mb-3">
            <input type="text" class="form-control" id="fullName" placeholder="John Doe" [(ngModel)]="fullName" name="fullName" required>
            <label for="fullName">Full Name</label>
        </div>
        <div class="form-floating mb-3">
            <input type="email" class="form-control" id="email" placeholder="name@example.com" [(ngModel)]="email" name="email" required>
            <label for="email">Email address</label>
        </div>
        <div class="form-floating mb-3">
            <textarea class="form-control" id="comments" placeholder="Leave a comment here" style="height: 100px"
                [(ngModel)]="comments" name="comments" required></textarea>
            <label for="comments">Comments</label>
        </div>
        <div class="form-floating mb-3">
            <input type="url" class="form-control" id="gitLink" placeholder="https://github.com/yourusername" [(ngModel)]="gitLink" name="gitLink">
            <label for="gitLink">Git Link</label>
        </div>
        <div class="form-floating mb-3">
            <input type="url" class="form-control" id="videoLink" placeholder="https://drive.google.com/yourvideo" [(ngModel)]="videoLink" name="videoLink">
            <label for="videoLink">Video Drive Link</label>
        </div>
        <div class="mb-3">
            <button type="submit" class="btn btn-primary">Submit</button>
        </div>
    </form>
</div>
