import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
declare var $: any; // Import jQuery

@Component({
  selector: 'app-assigning-marks',
  templateUrl: './assigning-marks.component.html',
  styleUrls: ['./assigning-marks.component.css']
})
export class AssigningMarksComponent {

  Details: any[] = [];
  filteredDetails: any[] = [];
  searchTerm: string = '';
  selectedStudentId: string = ''; // New property to store the selected student's ID
  marks: number = 0; // New property to store the entered marks

  constructor(private http: HttpClient) {
    this.getAllDetails();
  }

  getAllDetails() {
    this.http.get("http://localhost:9000/student/get").subscribe((resultData: any) => {
      console.log(resultData);
      this.Details = resultData.data;
    });
  }

  searchAttendance() {
    this.filteredDetails = this.Details.filter(student => {
      return student.fullName.toLowerCase().includes(this.searchTerm.toLowerCase());
    });
  }

  // Function to open the modal and set the selected student's ID
  openAssignMarksModal(id: string) {
    this.selectedStudentId = id;
    this.marks = 0; // Clear the marks input field
    $('#assignMarksModal').modal('show'); // Show the modal using jQuery
  }

  // Function to update student marks
  assignMarks() {
    const id = this.selectedStudentId;
    const updatedMarks = this.marks;

    // Make an HTTP request to update the student's marks
    this.http.patch('http://localhost:9000/student/update', { id, marks: updatedMarks })
      .subscribe(() => {
        this.getAllDetails();
        $('#assignMarksModal').modal('hide'); // Hide the modal using jQuery
      });
  }

  deleteStudent(id: string) {
    this.http.delete(`http://localhost:9000/student/delete/${id}`).subscribe(() => {
      this.getAllDetails();
    });
  }
}
<div class="container">
    <div class="text-center">
        <p class="fs-2">Student Details</p>
    </div>

    <div class="input-group mb-4">
        <input class="form-control" id="inputGroupSelect04" placeholder="Student Name" [(ngModel)]="searchTerm">
        <button class="btn btn-primary" type="button" (click)="searchAttendance()">Search</button>
    </div>

    <div class="row">
        <div class="col-md-4 mb-4" *ngFor="let student of filteredDetails">
            <div class="card h-100">
                <div class="card-header">
                    {{ student.fullName }}
                </div>
                <div class="card-body">
                    <h6 class="card-title">{{ student.email }}</h6>
                    <a class="card-text" [href]="student.gitLink" target="_blank">GitHub Link</a> <br>
                    <a class="card-text" [href]="student.videoLink" target="_blank">Video Link</a>
                    <h6 class="card-text">Marks: {{ student.marks }}</h6>
                </div>
                <div class="card-footer">
                    <div class="row">
                        <div class="col-md-6">
                            <button class="btn btn-success btn-block" (click)="openAssignMarksModal(student._id)">Assign
                                Marks</button>
                        </div>
                        <div class="col-md-6">
                            <button class="btn btn-danger btn-block"
                                (click)="deleteStudent(student._id)">Delete</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
